export const style = {
  bg: `h-screen w-s p-4 bg-gradient-to-r from-red-400 to-yellow-200`,
  container: `bg-slate-100 max-w-[500px] w-full  m-auto rounded-md shadow-xl 
      p-4 flex flex-col items-center`,
  heading: `text-3xl font-bold text-center text-gray-900 p-2`,
  form: `flex flex-col justify-between`,
  input: `border p-2 w-full text-xl m-2 rounded-lg`,
  button: `border p-4 ml-2 sm:mt-0 bg-green-400 rounded-lg mx-auto`,
  count: `text-center p-2`,
};
